import {useState} from "react";

function Square() {
    const [value, setValue] = useState(null);

    function handleClick(){
        setValue('X!');
    }
//  function Square({ value }) -> Square 컴포넌트에 value prop을 전달할 수 있음
//     == Square 컴포넌트를 Board 에서 전달할 value를 읽도록 수정하는 것
    return (
        <button className="square"
                    onClick={handleClick}
        >
            {value}
        </button>
    );
}

//  1번째 줄은 Square 함수를 정의
// export default function Square() {
  //  Java Script 의 export 키워드는 이 함수를 파일 외부에서 접근할 수 있도록 만듦
  //  default 키워드는 코드를 사용하는 다른 파일에서 이 함수가 파일의 주요 함수임을 알려줌

/*   -> Square를 9개 만들어서 이름이 Square 인 컴포넌트가
         더이상 하나의 사각형이 아니게 되었기에
         문제 수정을 위해 Board 로 이름 수정   */
export default function Board() {

  /*
// 2번째 줄은 버튼을 반환
  return <button className="square">X</button>;
  //  return 키워드는 해당 키워드 뒤에 오는 모든 것이 함수 호출자에게 값으로 반환됨을 의미
  //  <button> : JSX 엘리먼트. Java Script 와 HTML 태그의 조합으로 표시할 내용을 설명
  //  className="square" : 버튼 prop 또는 프로퍼티로, CSS에 버튼의 스타일을 지정하는 방법을 알려줌
  //  X : 버튼 내부에 표시되는 텍스트
  //  </button> : JSX 엘리먼트를 닫아 버튼 내부에 다음 콘텐츠를 배치해서는 안 됨을 나타냄
   */

  return (
      <>
          <div className="board-row">
              <Square />
              <Square />
              <Square />
          </div>
          <div className="board-row">
              <Square />
              <Square />
              <Square />
          </div>
          <div className="board-row">
              <Square />
              <Square />
              <Square />
          </div>
      </>
  );


}
